import { CanvasTestHelper } from '@app/classes/canvas-test-helper';
import { Color } from '@app/classes/color';
import { PaintCommand } from '@app/classes/commands/paint-command';
import { DrawingService } from '@app/services/drawing/drawing.service';
import { PaintNonContiguous } from './paint-non-contiguous';

describe('PaintNonContiguous', () => {
    let paintNonContiguous: PaintNonContiguous;
    let canvasTestHelper: CanvasTestHelper;
    let canvas: HTMLCanvasElement;
    let ctx: CanvasRenderingContext2D;
    const TRANSPARENT_BACKGROUND_PIXEL_NUMBER = 0x00000000;
    const BLACK_BACKGROUND_PIXEL_NUMBER = 0xff000000;
    const CANVAS_SIZE = 250;

    let drawServiceSpy: jasmine.SpyObj<DrawingService>;

    beforeEach(() => {
        drawServiceSpy = jasmine.createSpyObj('DrawingService', ['clearCanvas']);
        drawServiceSpy.clearCanvas.and.callThrough();
        canvasTestHelper = new CanvasTestHelper();
        canvas = canvasTestHelper.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
        ctx = canvas.getContext('2d') as CanvasRenderingContext2D;
        paintNonContiguous = new PaintNonContiguous();
    });

    it('should paint the whole canvas', () => {
        drawServiceSpy.clearCanvas(ctx);
        let pixelBuffer = new Uint32Array(ctx.getImageData(0, 0, canvas.width, canvas.height).data.buffer);
        expect(pixelBuffer.some((color: number) => color !== TRANSPARENT_BACKGROUND_PIXEL_NUMBER)).toBeFalse();

        new PaintCommand(ctx, paintNonContiguous.paint(ctx, { x: canvas.width, y: canvas.height }, 0, new Color(0, 0, 0))).do();

        pixelBuffer = new Uint32Array(ctx.getImageData(0, 0, canvas.width, canvas.height).data.buffer);
        expect(pixelBuffer.some((color: number) => color !== BLACK_BACKGROUND_PIXEL_NUMBER)).toBeFalse();
    });

    it('should paint inside circle', async () => {
        const tolerance = 10;
        drawServiceSpy.clearCanvas(ctx);

        const startImage = new Image();
        await new Promise((resolve) => {
            startImage.src = beforeContiguousFillURL;
            startImage.onload = () => {
                ctx.drawImage(startImage, 0, 0);
                resolve(null);
            };
        });

        new PaintCommand(ctx, paintNonContiguous.paint(ctx, { x: 60, y: 150 }, tolerance, new Color(0, 0, 0))).do();

        expect(canvas.toDataURL()).toEqual(afterContiguousFillURL);
    });

    it('image should not be the same with diffrent tolerance', async () => {
        const tolerance = 50;
        drawServiceSpy.clearCanvas(ctx);

        const startImage = new Image();
        await new Promise((resolve) => {
            startImage.src = beforeContiguousFillURL;
            startImage.onload = () => {
                ctx.drawImage(startImage, 0, 0);
                resolve(null);
            };
        });

        new PaintCommand(ctx, paintNonContiguous.paint(ctx, { x: 60, y: 150 }, tolerance, new Color(0, 0, 0))).do();

        expect(canvas.toDataURL()).not.toEqual(afterContiguousFillURL);
    });
});

const beforeContiguousFillURL =
    'data:image/png;base64,';

const afterContiguousFillURL =
    'data:image/png;base64,';
